```ts survey-web-app/.app/server/api/dashboard.ts
import { defineEventHandler, createError } from 'h3';
import { readItems, readSingleton } from '@directus/sdk';
import clientt from '../../utils/directus'
import { decodeJwt } from '../../utils/jwt';

interface DayCounts {
  [key: number]: number;
}

interface Announcement {
  id: string;
  title: string;
  content: string;
  user_created: {
    id: string;
    first_name: string;
    last_name: string;
  };
  date_created: string;
}

interface DashboardResponse {
  days_months_series: number[];
  freq_today: number;
  today_freq_diff: number;
  percentage_today: string;
  hour_counts_series: number[];
  announcements: Announcement[];
}

interface SurveyWebAppConfig {
  target: number;
}

interface ApiError extends Error {
  statusCode?: number;
}

export default defineEventHandler(async (event): Promise<DashboardResponse> => {
  try {
    const token = getCookie(event, 'directus_token')
    if (!token) {
      throw createError({
        statusCode: 401,
        message: 'Unauthorized - No token provided'
      })
    }

    const decodedToken = decodeJwt(token)
    if (!decodedToken?.id) {
      throw createError({
        statusCode: 400,
        message: 'Invalid token - No user ID found'
      })
    }

    const now = new Date();
    const year = now.getFullYear();
    const month = now.getMonth() + 1;

    const client = clientt()

    // Fetch config and announcements in parallel
    const [config, announcements] = await Promise.all([
      client.request(readSingleton('survey_web_app_config', {
        fields: ['target']
      })) as Promise<SurveyWebAppConfig>,
      client.request(readItems('announcements', {
        fields: ['*', 'user_created.*']
      })) as Promise<Announcement[]>
    ])

    const submissions_month = await client.request(readItems('survey_submissions', {
      fields: ['date_created'],
      filter: {
        _and: [{
            "year(date_created)": {
              _eq: year
            }
          },
          {
              "month(date_created)": {
              _eq: month
            }
          },
          {
            user: {
              _eq: decodedToken.id
            }
          }
        ]
      }
    }))

    // Parse dates and group by day of the month
    const parsedDates = submissions_month.map(entry => new Date(entry.date_created));
    const dayCounts: DayCounts = {};

    // Count occurrences for each day
    for (const date of parsedDates) {
      const day = date.getUTCDate();
      dayCounts[day] = (dayCounts[day] || 0) + 1;
    }

    // Get today's date and initialize the result
    const today = now.getUTCDate();
    const currDay = now.getUTCDay();
    const currMonth = now.getUTCMonth();
    const currYear = now.getUTCFullYear();

    const days_months_series = Array.from({ length: today }, (_, i) => dayCounts[i + 1] || 0);
    const freq_today = days_months_series[today - 1] || 0;

    const today_freq_diff = config.target - today;
    const percentage_today = config.target > 0 ? (freq_today / config.target) * 100 : 0;
    const currentHour = (now.getUTCHours() + 1) % 24;

    const hour_counts_series = Array(currentHour + 1).fill(0);

    // Count occurrences for each hour
    for (const date of parsedDates) {
      const day = date.getUTCDay()
      const month = date.getUTCMonth()
      const year = date.getFullYear()
      const hour = date.getUTCHours();
      if (hour <= currentHour && day === currDay && month === currMonth && year === currYear) {
        hour_counts_series[hour]++;
      }
    }

    return {
      days_months_series,
      freq_today,
      today_freq_diff,
      percentage_today: percentage_today.toFixed(2),
      hour_counts_series,
      announcements
    }
  } catch(error) {
    console.error('Dashboard API Error:', error)
    const apiError = error as ApiError
    throw createError({
      statusCode: apiError.statusCode || 500,
      message: apiError.message || 'Internal Server Error'
    })
  }
})
