```ts survey-web-app/.app/composables/directus.ts
import { createSharedComposable } from "@vueuse/core"

/**
 * ðŸ‡ Nuxt Directus wrapper, the intuitive way to interact with Directus across the application.
 *
 * @see https://nuxt-directus.de
 */
function _useDirectusClient() {
  const auth = useDirectusAuth()
  const user = useDirectusUser()
  const token = useDirectusToken()
  const items = useDirectusItems()

  // Authentication operations
  const login = async (email: string, password: string) => {
    return await auth.login({ email, password })
  }

  const logout = async () => {
    await auth.logout()
    await navigateTo('/auth')
  }

  const getToken = () => {
    return token.token.value
  }

  const refreshToken = async () => {
    return await token.refreshTokens()
  }

  // Data operations
  const getItems = async <T>(collection: string, query?: any) => {
    return await items.getItems<T>({ collection, params: query })
  }

  const getItem = async <T>(collection: string, id: string, query?: any) => {
    return await items.getItems<T>({ collection, params: { filter: { id: { _eq: id } }, ...query } })
  }

  const create = async <T>(collection: string, item: any) => {
    return await items.createItems<T>({ collection, items: [item] })
  }

  const update = async <T>(collection: string, id: string, item: any) => {
    return await items.updateItem<T>({ collection, id, item })
  }

  const remove = async (collection: string, id: string) => {
    return await items.deleteItems({ collection, items: [id] })
  }

  return {
    /**
     * @internal
     * the `useDirectusAuth` composable representation
     */
    _auth: readonly(auth),
    /**
     * @internal
     * the `useDirectusToken` composable representation
    */
   _token: readonly(token),


    user: readonly(user),
    login,
    logout,
    refreshToken,

    getToken,

    // Data operations
    getItems,
    getItem,
    create,
    update,
    remove
  }
}

export const useDirectusClient = createSharedComposable(_useDirectusClient)
